<?php defined("BASEPATH") OR exit("No direct script access allowed"); if (!function_exists("get_sendgrid_api_key")) { function get_sendgrid_api_key($key_type = "primary") { $CI =& get_instance(); if ($key_type === "alt") { $api_key = $CI->config->item("sendgrid_api_key_alt"); } else { $api_key = $CI->config->item("sendgrid_api_key"); } if (empty($api_key)) { if ($key_type === "alt") { $api_key = getenv("SENDGRID_API_KEY_ALT"); } else { $api_key = getenv("SENDGRID_API_KEY"); } } return $api_key; } } ?>
